(function($){$.fn.filestyle=function(options){if(typeof options=="object"||typeof options=="undefined"){var defaults={buttonText:"Choose file",textField:true,icon:false,classButton:"",classText:"",classIcon:"icon-folder-open"};var options=$.extend(defaults,options);return this.each(function(){var $this=$(this);$this.data("filestyle",true);$this.css({position:"fixed",top:"-100px",left:"-100px"}).parent().addClass("form-search").append((options.textField?'<input type="text" class="'+options.classText+'" disabled class="span2" /> ':"")+'<button type="button" class="btn '+options.classButton+'" >'+(options.icon?'<i class="'+options.classIcon+'"></i> ':"")+options.buttonText+"</button>");$this.change(function(){$this.parent().children(":text").val($(this).val())});$this.parent().children(":button").click(function(){$this.click()})})}else{return this.each(function(){var $this=$(this);if($this.data("filestyle")==true&&options=="clear"){$this.parent().children(":text").val("");$this.val("")}else{console.error("Method filestyle not defined!")}})}}})(jQuery);
